SET SQL_MODE = "NO_AUTO_VALUE_ON_ZERO";




drop table if exists `configuration`;
CREATE TABLE `configuration` (
  `configuration_id` int(11) NOT NULL AUTO_INCREMENT,
  `configuration_title` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `configuration_key` varchar(64) COLLATE utf8_unicode_ci NOT NULL,
  `configuration_value` text COLLATE utf8_unicode_ci DEFAULT NULL,
  `configuration_description` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `configuration_group_id` int(11) NOT NULL DEFAULT 0,
  `sort_order` int(5) NOT NULL DEFAULT 0,
  `last_modified` datetime DEFAULT NULL,
  `date_added` datetime DEFAULT NULL,
  `use_function` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `set_function` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  PRIMARY KEY (`configuration_id`)
);


drop table if exists `currencies`;
CREATE TABLE `currencies` (
  `currencies_id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(32) COLLATE utf8_unicode_ci NOT NULL,
  `code` char(3) COLLATE utf8_unicode_ci NOT NULL,
  `symbol_left` varchar(12) COLLATE utf8_unicode_ci DEFAULT NULL,
  `symbol_right` varchar(12) COLLATE utf8_unicode_ci DEFAULT NULL,
  `decimal_point` char(1) COLLATE utf8_unicode_ci DEFAULT NULL,
  `thousands_point` char(1) COLLATE utf8_unicode_ci DEFAULT NULL,
  `decimal_places` char(1) COLLATE utf8_unicode_ci DEFAULT NULL,
  `value` float(13,8) DEFAULT NULL,
  `last_updated` datetime DEFAULT NULL,
  PRIMARY KEY (`currencies_id`)
);


drop table if exists `customers_info_fields`;
CREATE TABLE `customers_info_fields` (
  `info_id` int(11) NOT NULL AUTO_INCREMENT,
  `uniquename` varchar(40) COLLATE utf8_unicode_ci DEFAULT NULL,
  `active` varchar(1) COLLATE utf8_unicode_ci NOT NULL DEFAULT 'Y',
  `system` varchar(1) COLLATE utf8_unicode_ci NOT NULL DEFAULT 'N',
  `locked` varchar(1) COLLATE utf8_unicode_ci NOT NULL DEFAULT 'N',
  `show_label` varchar(1) COLLATE utf8_unicode_ci NOT NULL DEFAULT 'Y',
  `input_type` varchar(1) COLLATE utf8_unicode_ci NOT NULL DEFAULT 'T',
  `default_value` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `textbox_size` varchar(20) COLLATE utf8_unicode_ci NOT NULL,
  `textbox_min_length` int(6) NOT NULL DEFAULT 0,
  `textbox_max_length` int(50) NOT NULL DEFAULT 0,
  `required` varchar(1) COLLATE utf8_unicode_ci NOT NULL DEFAULT 'Y',
  `sort_order` smallint(6) NOT NULL DEFAULT 0,
  `options_values` text COLLATE utf8_unicode_ci DEFAULT NULL,
  `storage_type` varchar(10) COLLATE utf8_unicode_ci NOT NULL DEFAULT 'E',
  `display_page` varchar(64) COLLATE utf8_unicode_ci NOT NULL DEFAULT 'C,E',
  `extra_param` text COLLATE utf8_unicode_ci DEFAULT NULL,
  PRIMARY KEY (`info_id`)
);


drop table if exists `customers_info_fields_description`;
CREATE TABLE `customers_info_fields_description` (
  `info_id` int(11) NOT NULL DEFAULT 0,
  `label_text` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `input_title` mediumtext COLLATE utf8_unicode_ci DEFAULT NULL,
  `input_description` mediumtext COLLATE utf8_unicode_ci DEFAULT NULL,
  `error_text` varchar(400) COLLATE utf8_unicode_ci DEFAULT NULL,
  `languages_id` int(11) NOT NULL DEFAULT 0,
  `unique_id` int(11) NOT NULL,
  PRIMARY KEY (`info_id`)
);


drop table if exists `email_messages`;
CREATE TABLE `email_messages` (
  `message_id` int(11) NOT NULL AUTO_INCREMENT,
  `message_send` char(3) COLLATE utf8_unicode_ci DEFAULT NULL,
  `message_subject` varchar(100) COLLATE utf8_unicode_ci DEFAULT NULL,
  `message_reply_to` varchar(64) COLLATE utf8_unicode_ci DEFAULT NULL,
  `message_text` longtext COLLATE utf8_unicode_ci NOT NULL,
  `message_format` char(1) COLLATE utf8_unicode_ci NOT NULL DEFAULT 'T',
  `message_type` char(3) COLLATE utf8_unicode_ci NOT NULL,
  PRIMARY KEY (`message_id`)
);


drop table if exists `general_templates`;
CREATE TABLE `general_templates` (
  `template_id` int(11) NOT NULL AUTO_INCREMENT,
  `template_type` varchar(4) COLLATE utf8_unicode_ci DEFAULT 'TICE',
  `template_width` decimal(6,4) DEFAULT 0.0000,
  `template_height` decimal(6,4) DEFAULT 0.0000,
  `template_content` text COLLATE utf8_unicode_ci DEFAULT NULL,
  PRIMARY KEY (`template_id`)
);


drop table if exists `main_page_description`;
CREATE TABLE `main_page_description` (
  `page_id` int(11) unsigned NOT NULL DEFAULT 0,
  `page_name` varchar(250) COLLATE utf8_unicode_ci NOT NULL,
  `description` text COLLATE utf8_unicode_ci NOT NULL,
  `language_id` int(11) unsigned NOT NULL DEFAULT 1,
  PRIMARY KEY (`page_id`,`language_id`)
);


drop table if exists `main_pages`;
CREATE TABLE `main_pages` (
  `page_id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `parent_id` int(11) unsigned NOT NULL DEFAULT 0,
  `date_created` datetime DEFAULT NULL,
  `date_modified` datetime DEFAULT NULL,
  `sort_order` int(11) NOT NULL DEFAULT 0,
  `page_status` tinyint(1) NOT NULL DEFAULT 0,
  PRIMARY KEY (`page_id`)
);






drop table if exists `template`;
CREATE TABLE `template` (
  `template_id` int(11) NOT NULL AUTO_INCREMENT,
  `template_name` varchar(64) COLLATE utf8_unicode_ci DEFAULT '0',
  `date_added` datetime DEFAULT NULL,
  `last_modified` datetime DEFAULT NULL,
  `template_image` varchar(64) COLLATE utf8_unicode_ci DEFAULT NULL,
  `site_width` varchar(5) COLLATE utf8_unicode_ci NOT NULL DEFAULT '100%',
  `side_box_left_width` varchar(64) COLLATE utf8_unicode_ci NOT NULL DEFAULT '0',
  `side_box_right_width` varchar(64) COLLATE utf8_unicode_ci NOT NULL DEFAULT '0',
  `include_column_left` varchar(64) COLLATE utf8_unicode_ci NOT NULL DEFAULT 'yes',
  `include_column_right` varchar(64) COLLATE utf8_unicode_ci NOT NULL DEFAULT 'yes',
  `box_width_left` varchar(4) COLLATE utf8_unicode_ci NOT NULL DEFAULT '260',
  `header_banner` text COLLATE utf8_unicode_ci DEFAULT NULL,
  `show_breadcrumb` varchar(6) COLLATE utf8_unicode_ci DEFAULT 'yes',
  `active` char(1) COLLATE utf8_unicode_ci NOT NULL DEFAULT '1',
  `show_header_pane` varchar(6) COLLATE utf8_unicode_ci DEFAULT 'no',
  `languages_in_header` char(3) COLLATE utf8_unicode_ci DEFAULT 'no',
  `container_border` char(3) COLLATE utf8_unicode_ci NOT NULL DEFAULT 'yes',
  `cart_in_header` varchar(6) COLLATE utf8_unicode_ci DEFAULT 'no',
  `show_page_descriptions` char(3) COLLATE utf8_unicode_ci NOT NULL DEFAULT 'yes',
  `module_one` varchar(64) COLLATE utf8_unicode_ci NOT NULL,
  `module_two` varchar(64) COLLATE utf8_unicode_ci NOT NULL,
  `module_three` varchar(64) COLLATE utf8_unicode_ci NOT NULL,
  `module_four` varchar(64) COLLATE utf8_unicode_ci NOT NULL,
  `module_five` varchar(64) COLLATE utf8_unicode_ci NOT NULL,
  `module_six` varchar(64) COLLATE utf8_unicode_ci NOT NULL,
  `customer_greeting` char(3) COLLATE utf8_unicode_ci NOT NULL DEFAULT 'yes',
  `edit_customer_greeting_personal` text COLLATE utf8_unicode_ci NOT NULL,
  `edit_customer_greeting_personal_relogon` text COLLATE utf8_unicode_ci NOT NULL,
  `edit_greeting_guest` text COLLATE utf8_unicode_ci NOT NULL,
  `show_topbar` varchar(6) COLLATE utf8_unicode_ci DEFAULT 'no',
  `template_color` varchar(28) COLLATE utf8_unicode_ci NOT NULL,
  `header_height` int(11) NOT NULL,
  PRIMARY KEY (`template_id`),
  KEY `IDX_TEMPLATE_NAME` (`template_name`)
);



