UPDATE `email_messages` SET `message_text`='<p>Dear %%First_Name%%,</p><p>A request to reset your password has been received at %%Store_Name%%</p><p>To reset your password use the following link: <br /><a href=\"%%Login_Password%%\">%%Login_Password%%</a></p><p>The link is valid for %%Login_Email%% hours.</p><p>If you did not request a change of password then you need do nothing.</p><p>If you have further trouble accessing your account, please contact us at: %%Store_Email%%</p><p>With thanks, %%Store_Owner%%</p>' WHERE `message_type`='CUX';
UPDATE `email_messages` SET `message_format` = 'B' WHERE `message_type`='CUX';